(function($){$(document).ready(function(){var media_frame;$(document.body).on('click.ewcOpenMediaManager','.custom-media-button',function(e){e.preventDefault();if(media_frame){media_frame.open();return;}media_frame=wp.media.frames.media_frame=wp.media({className:'media-frame ewc-media-frame',frame:'select',multiple:false,title:'Choose or Upload Image',library:{type:'image'},button:{text:'Insert Image'}});media_frame.on('select',function(){var media_attachment=media_frame.state().get('selection').first().toJSON();$('.custom-media-url').val(media_attachment.url);$('.custom-media-url').trigger('change');});media_frame.open();});});})(jQuery);