(function($){$(document).ready(function(){var media_frame;var target_input;$(document.body).on('click.ajvOpenMediaManager','.custom-media-button',function(e){e.preventDefault();var target_input=$(this).prev().attr('id');media_frame=wp.media.frames.media_frame=wp.media({className:'media-frame ajv-media-frame',frame:'select',multiple:false,title:ajv_image_upload.frame_title,library:{type:'image'},button:{text:ajv_image_upload.frame_button}});media_frame.on('select',function(){var media_attachment=media_frame.state().get('selection').first().toJSON();$('#'+target_input).val(media_attachment.url);$('#'+target_input).trigger('change');});media_frame.open();});});})(jQuery);